import React from 'react';
import ReactRoundedImage from "react-rounded-image";
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import AlbumIcon from '@material-ui/icons/Album';
import InfoIcon from '@material-ui/icons/Info';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import './homepage_styles.css';
const useStyles = makeStyles({
    list: {
      width: 250,
    },
    fullList: {
      width: 'auto',
    },
});
const HomePage = ({handleLogout,user}) => {
    // console.log({user});

    const classes = useStyles();
    const [state, setState] = React.useState({
      left: false,
    });

    const toggleDrawer = (anchor, open) => (event) => {
      if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
        return;
      }

      setState({ ...state, [anchor]: open });
    };

    const list = (anchor) => (
      <div
        className={clsx(classes.list, {
          [classes.fullList]: anchor === 'top' || anchor === 'bottom',
        })}
        role="presentation"
        onClick={toggleDrawer(anchor, false)}
        onKeyDown={toggleDrawer(anchor, false)}
      >
        <List>
          {['Profile', 'Albums', 'About', 'Logout'].map((text, index) => (
            <ListItem button key={text}>
              <ListItemIcon>{index === 0 ? <AccountCircleIcon /> : (index===1) ? <AlbumIcon/> : (index===2) ? <InfoIcon/> : <ExitToAppIcon/> }</ListItemIcon>
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>

      </div>
    );

    return(
        <section className="hero">
        <nav>
        <div className="profile">
            <ReactRoundedImage imageWidth="80"
            imageHeight="80" roundedColor="orange" roundedSize="15" image={user.photoURL} />
         </div>
         <h2> Welcome {user.displayName}</h2>
         <button onClick={handleLogout}>Logout</button>
     </nav>   
     <div>
     {['left'].map((anchor) => (
       <React.Fragment key={anchor}>
         <Button onClick={toggleDrawer(anchor, true)}>{anchor}</Button>
         <Drawer anchor={anchor} open={state[anchor]} onClose={toggleDrawer(anchor, false)}>
           {list(anchor)}
         </Drawer>
       </React.Fragment>
     ))}
   </div>




       </section>
    )
}

export default HomePage;
